import React, { useState } from 'react';
import './App.css';
import Navigationbar from './components/Navigationbar';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import ListingPage from './components/pages/ListingPage';
import ChallengeDetails from './pages/ChallengeDetails';

// Define the structure of a challenge using an interface
interface Challenge {
  id: number;
  challengeName: string;
  startDate: string;
  endDate: string;
  description: string;
  image: string;
  levelType: 'Easy' | 'Medium' | 'Hard';
}

function App() {
  // Sample state for challenges, adjust the initial state accordingly.
  const [challenges, setChallenges] = useState<Challenge[]>([
    // Add mock data or fetch from an API
  ]);

  // Function to add a challenge
  const addChallenge = (newChallenge: Challenge) => {
    setChallenges([...challenges, newChallenge]);
  };

  // Function to update an existing challenge
  const updateChallenge = (updatedChallenge: Challenge) => {
    setChallenges(challenges.map(challenge =>
      challenge.id === updatedChallenge.id ? updatedChallenge : challenge
    ));
  };

  // Function to delete a challenge
  const deleteChallenge = (challengeId: number) => {
    setChallenges(challenges.filter(challenge => challenge.id !== challengeId));
  };

  return (
    <>
      <Router>
        <div className="App">

          <Navigationbar />
          <Routes>
             <Route
              path="/"
              element={
                <ListingPage
                 addChallenge={addChallenge}
                 challenges={challenges} 
                />}
            /> 
            <Route
              path="/challenge/:id"
              element={
              <ChallengeDetails
               challenges={challenges} 
               updateChallenge={updateChallenge} 
               deleteChallenge={deleteChallenge} 
               />}
            />
          </Routes>
        </div>
      </Router>
    </>
  );
}

export default App;
