import React, { useState } from 'react';

interface Challenge {
  id: number;
  challengeName: string;
  startDate: string;
  endDate: string;
  description: string;
  image: string;
  levelType: 'Easy' | 'Medium' | 'Hard';
  status: 'Active' | 'Upcoming' | 'Past'
}

interface ChallengeDetailsProps {
  challenges: Challenge[];
  updateChallenge: (updatedChallenge: Challenge) => void;
  closeModal: () => void;
}

const ChallengeDetails: React.FC<ChallengeDetailsProps> = ({ challenges, updateChallenge, closeModal }) => {


  const [challengeName, setChallengeName] = useState<string>(challenge?.challengeName || '');
  const [startDate, setStartDate] = useState<string>(challenge?.startDate || '');
  const [endDate, setEndDate] = useState<string>(challenge?.endDate || '');
  const [description, setDescription] = useState<string>(challenge?.description || '');
  const [image, setImage] = useState<string>(challenge?.image || '');
  const [levelType, setLevelType] = useState<'Easy' | 'Medium' | 'Hard'>(challenge?.levelType || 'Easy');

  const handleEdit = () => {
    if (challenge) {
      const updatedChallenge: Challenge = {
        ...challenge,
        challengeName,
        startDate,
        endDate,
        description,
        image,
        levelType,
      };
      updateChallenge(updatedChallenge);
      navigate('/');
    }
    closeModal()
  };



  return (
    <div>
      <h2>Challenge Details</h2>
      <div>
        <img src={challenge?.image} alt={challenge?.challengeName} />
        <div>
          <label>Challenge Name</label>
          <input type="text" value={challengeName} onChange={(e) => setChallengeName(e.target.value)} />
        </div>
        <div>
          <label>Start Date</label>
          <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} />
        </div>
        <div>
          <label>End Date</label>
          <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} />
        </div>
        <div>
          <label>Description</label>
          <textarea value={description} onChange={(e) => setDescription(e.target.value)}></textarea>
        </div>
        <div>
          <label>Image</label>
          <input type="file" onChange={(e) => setImage(URL.createObjectURL(e.target.files![0]))} />
        </div>
        <div>
          <label>Level Type</label>
          <select value={levelType} onChange={(e) => setLevelType(e.target.value as 'Easy' | 'Medium' | 'Hard')}>
            <option value="Easy">Easy</option>
            <option value="Medium">Medium</option>
            <option value="Hard">Hard</option>
          </select>
        </div>
        <button onClick={handleEdit}>Edit Details</button>
      </div>
    </div>
  );
};

export default ChallengeDetails;
