import React, { useState } from 'react';
import { Container, Row, Col, Button, Modal } from 'react-bootstrap';
import rocketimage from './assets/icons/rocket.svg';
import ChallengeDetails from './ChallengeDetails';

// Define the structure of a Challenge object
interface Challenge {
  id: number;
  challengeName: string;
  startDate: string;
  endDate: string;
  description: string;
  image: string;
  levelType: 'Easy' | 'Medium' | 'Hard';
  status: 'Active' | 'Upcoming' | 'Past';
}

// Define the props structure for EditDetails component
interface EditDetailsProps {
  challenges: Challenge[];
  updateChallenge: (updatedChallenge: Challenge) => void;
  deleteChallenge: (id: number) => void;
}




const EditDetails: React.FC<EditDetailsProps> = ({ challenges,updateChallenge }) => {
  const [show, setShow] = useState<boolean>(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const { id } = useParams<{ id: string }>();
  const challenge = challenges.find(ch => ch.id === parseInt(id!));
  const navigate = useNavigate();

  const handleDelete = () => {
    if (challenge) {
      deleteChallenge(challenge.id);
      navigate('/');
    }
  };

  return (
    <>
      <Container fluid className="bg-dark text-white p-5">
        <Row>
          <Col md={8}>
            <h1>Edit Challenge Details</h1>
            <p>
              Use this interface to edit the details of your AI/Data Science challenges. Keep your challenges updated to reflect the latest information.
            </p>
            <Button variant="primary" onClick={handleShow}>
              Edit Details
            </Button>
          </Col>
          <Col md={4} className="text-center">
            <img src={challenge?.image} alt="Rocket" style={{ width: '50%' }} />
          </Col>
        </Row>
      </Container>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Edit Challenge Details</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* Assuming `ChallengeDetails` is where you handle challenge editing */}
          <ChallengeDetails challenges={challenges} updateChallenge={updateChallenge} closeModal={handleClose} />
        </Modal.Body>
      </Modal>
      <button onClick={handleDelete}>Delete Challenge</button>
    </>
  );
};

export default EditDetails;
